const createAlbumComponent=e=>{const t=document.createElement("div");t.classList.add("imggroup");const n=document.createElement("img");n.src=e.cover_medium,t.appendChild(n);const c=document.createElement("figure");t.appendChild(c);const a=document.createElement("blockquote");for(a.innerHTML=e.title,c.appendChild(a),i=0;i<e.stars;i++){const e=document.createElement("img");e.classList.add("star"),e.src="https://assets.codepen.io/296057/fem-star.svg",c.appendChild(e)}const o=document.createElement("figcaption");c.appendChild(o);const d=document.createElement("cite");return d.innerText=new Date(e.release_date).toLocaleDateString("pt-BR"),o.appendChild(d),t};fetch("https://api.deezer.com/artist/1606874/albums").then(e=>e.json()).then(e=>{e.data.forEach(e=>{const t=createAlbumComponent(e);document.getElementById("albums").appendChild(t)})});